    content: '';
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 5px;
    background: linear-gradient(90deg, var(--color-secundario), var(--color-detalle));
    transform: scaleX(0);
    transform-origin: left;
    transition: transform 0.4s ease;
    pointer-events: none;
}

/* Solo aplicar hover a tarjetas que NO están en flip */
.accion-card:not(.accion-flip-front):not(.accion-flip-back)::before {
    /* Esto solo afecta tarjetas normales, no las de flip */
}

.accion-card:not(.accion-flip-front):not(.accion-flip-back):hover::before {
    transform: scaleX(1);
}

.accion-card:not(.accion-flip-front):not(.accion-flip-back):hover {
    transform: translateY(-10px);
    box-shadow: 0 15px 40px rgba(20, 91, 171, 0.15);
}

/* Para las tarjetas de flip: NO aplicar transformación en hover */
.accion-flip-front .accion-card,
.accion-flip-back .accion-card {
    transform: none !important;
}

.accion-flip-front .accion-card:hover,
.accion-flip-back .accion-card:hover {
    transform: none !important;
